/// Get the OtherGraphemeExtend attribute for a Unicode code-point.
///
/// # Arguments
///  - `code_point` A code-point in the form of a rust `char`.
///
/// # Returns
/// bool value
#[must_use] pub const fn get_other_grapheme_extend(code_point: char) -> bool
{
    const INDEX_MASK : usize = (1 << OTHER_GRAPHEME_EXTEND_INDEX_BITS) - 1;
    const COLUMN_MASK : usize = 1;

    let code_point_value = code_point as usize;
    let code_point_lo = code_point_value % OTHER_GRAPHEME_EXTEND_CHUNK_SIZE;
    let mut code_point_hi = code_point_value / OTHER_GRAPHEME_EXTEND_CHUNK_SIZE;
    if code_point_hi > OTHER_GRAPHEME_EXTEND_INDEX_LEN - 1 {
        code_point_hi = OTHER_GRAPHEME_EXTEND_INDEX_LEN - 1;
    }

    let index_offset = code_point_hi * OTHER_GRAPHEME_EXTEND_INDEX_BITS;
    let index_byte_offset = index_offset / 8;
    let index_bit_offset = index_offset % 8;
    let mut index: usize = 0;
    index |= (OTHER_GRAPHEME_EXTEND_DATA[OTHER_GRAPHEME_EXTEND_INDEX_BYTE_OFFSET + index_byte_offset + 1] as usize) << 8;
    index |= (OTHER_GRAPHEME_EXTEND_DATA[OTHER_GRAPHEME_EXTEND_INDEX_BYTE_OFFSET + index_byte_offset + 0] as usize) << 0;
    index >>= index_bit_offset;
    index &= INDEX_MASK;

    let column_offset = (index * OTHER_GRAPHEME_EXTEND_CHUNK_SIZE + code_point_lo) * OTHER_GRAPHEME_EXTEND_COLUMN_BITS;
    let column_byte_offset = column_offset / 8;
    let column_bit_offset = column_offset % 8;

    let mut value: usize = 0;
    value |= (OTHER_GRAPHEME_EXTEND_DATA[column_byte_offset + 0] as usize) << 0;
    value >>= column_bit_offset;
    value &= COLUMN_MASK;

    return match value {
        0 => false,
        _ => true,
    };
}

#[cfg(all(test, not(debug_assertions)))]
#[test]
fn other_grapheme_extend_full_coverage()
{
    for c in '\u{0000}'..='\u{d7ff}' {
        let _ = get_other_grapheme_extend(c);
    }
    for c in '\u{e000}'..='\u{10ffff}' {
        let _ = get_other_grapheme_extend(c);
    }
}

